import React, { useState, useEffect } from 'react';
import useSocket from 'hooks/useSocket'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';

export default function FormDialog() {
    const socket: any = useSocket('http://127.0.0.1:3000')

    const [open, setOpen] = useState(true);
    const [username, setUsername] = useState('');

    useEffect(() => {
        const handleUpdateMessages = (message: any) => {
            setMessages(messages => [...messages, message])
        }

        if (socket) {
            socket.on('UPDATE_MESSAGES', handleUpdateMessages)
        }
    }, [socket])

    const onSubmit = () => {
        if (socket) {
            console.log(username)
            socket.emit('ADD_USER', username)
        }
        setOpen(false);
    };

    const onChangeUsernameInput = (event: any) => {
        setUsername(event.target.value)
    }

    return (
        <div>
            <Dialog open={open} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Set Username</DialogTitle>
                <DialogContent>
                <TextField
                    onChange={onChangeUsernameInput}
                    autoFocus
                    margin="dense"
                    id="name"
                    label="Username"
                    type="text"
                    fullWidth
                />
                </DialogContent>
                <DialogActions>
                <Button onClick={onSubmit}  color="primary">
                    Enter
                </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}
