import React, { useState } from 'react';
import { createStyles, withStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import { FlatButton, DialogTitle, InputLabel, Input } from '@material-ui/core';
import { blue } from '@material-ui/core/colors';

const styles = createStyles({
  form: {
      margin: '10px',
  }
});

export interface IProps {
    classes: any,
}

const LoginForm: React.FC<IProps> = props => {
    const { classes } = props;

    const [shouldDialogOpen, setShouldDialogOpen] = useState(true);

    const onClose = () => {
        setShouldDialogOpen(false);
    }

    const actions = [
        <FlatButton
          type="submit"
          label="Submit"
          primary={true}
          />,
      ];

      return (
        <Dialog
          title="Dialog With Custom Width"
          modal={true}
          open={this.state.open}
          >
          <form action="/" method="POST" onSubmit={(e) => { e.preventDefault(); alert('Submitted form!'); this.handleClose(); } }>
            This dialog spans the entire width of the screen.
            <TextField name="email" hintText="Email" />
            <TextField name="pwd" type="password" hintText="Password" />
            <div style={{ textAlign: 'right', padding: 8, margin: '24px -24px -24px -24px' }}>
              {actions}
            </div>
          </form>
        </Dialog>
      );
}

export default withStyles(styles)(LoginForm)
