import React, { useState, useEffect } from "react"
import { createStyles, Paper, Avatar, Divider, Input, withStyles, Chip } from "@material-ui/core"
import Button from '@material-ui/core/Button'
import SendIcon from '@material-ui/icons/Send'
import { Message } from 'models/message/model'

const chatTextStyles = createStyles({
	root: {
		margin: '8px',
		height: 'calc(100% - 80px)',
	},
	chatInputPaper: {
		height: 'calc(100% - 64px)'
	},
	chatInput: {
		overflowY: 'scroll',
		height: '100%',
		padding: '16px',
	},
	composeInputPaper: {
        display: 'flex',
        marginTop: '16px',
	},
	composeInput: {
        padding: '16px',
	},
	sendIcon: {
		color: '#2196f3'
	},
	chatRoomMessage: {
		display: 'flex',
		marginBottom: '8px'
	},
	messageText: {
		padding: '8px 0px 0px 16px'
	},
	divider: {
		marginBottom: '8px'
	}
})

interface IProps {
    classes: any
    messages?: Message[]
    handleSendButton?: Function
}

const defaultProps: IProps = {
    classes: {},
    messages: [],

}

const ChatText: React.FC<IProps> = props => {

		const messages = props.messages! //TODO: Delete ! operator when props are done
		const classes = props.classes

        const [text, setText] = useState('')

        const handleTextChange = (event: any) => setText(event.target.value)
        const handleSendButton = (): any => {
            setText('')
            props.handleSendButton!(text)
        }

        const handleUserKeyPress = (event: any) => {
            const { keyCode } = event;

            if (keyCode === 13) {
                event.preventDefault();
                handleSendButton()
            }
        };

        useEffect(() => {
            window.addEventListener('keydown', handleUserKeyPress);

            return () => {
              window.removeEventListener('keydown', handleUserKeyPress);
            };
        });

        return (
			<section className={classes.root}>
				<Paper className={classes.chatInputPaper} elevation={4}>
					<div className={classes.chatInput}>

						{messages.map((message: Message) => {
							return (
								<React.Fragment key={message.id}>
                                    <div className={classes.chatRoomMessage}>
                                        <p><strong>Thomas</strong></p>
                                        <Avatar alt={message.userId} src={`https://avatars.dicebear.com/api/male/${message.userId}.svg`} />
                                        <div>
                                            <Chip label={message.text} />
                                        </div>
                                    </div>
								</React.Fragment>
							)
						})}

					</div>
				</Paper>
				<Paper className={classes.composeInputPaper} elevation={4}>
                    <Input 
                        classes={{ root: classes.composeInput }} multiline={true} value={text} 
                        fullWidth={true} disableUnderline={true} placeholder={'Send a message!'} 
                        onChange={handleTextChange}    
                    />
                    <Button
                        variant='contained'
                        color='primary'
                        onClick={handleSendButton}
                        endIcon={<SendIcon className={classes.searchIcon} />}
                    >
                        Send
                    </Button>
				</Paper>
			</section>
		)

}

ChatText.defaultProps = defaultProps

export default withStyles(chatTextStyles)(ChatText)
